/*
{
  hello
  test1
  hi(name:"Juan")
  greet(name:"Juan")
  task {
      _id
      title
      description
      number
  }
  users {
      _id
      firstname
      lastname
      age
    }
}
*/

// mutation modificar los objetos
/*
mutation {
    createTask(input: {
      title: "Task one"
      description: "Description one"
      number: 400
    }) {
      _id
      title
      description
      number
    }
  }
   createUser(input: {
      firstname: "Juan"
